    private void jMenuAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuAddActionPerformed
        // TODO add your handling code here:
        try 
        {
        if(!jRadioSingle.isSelected() &&
                !jRadioDouble.isSelected())
            {
                JOptionPane.showMessageDialog(null, "Select Single or Double Burger");
                jRadioSingle.requestFocusInWindow();
                return;
            }
            
        if(jtxtQuantity.getText() != "0")
            {
                order.setOrderQty(Integer.parseInt(jtxtQuantity.getText()));
            }
        
        if(jtxtQuantity.getText().isEmpty() ||
                Integer.parseInt(jtxtQuantity.getText()) < 1)
            {
                JOptionPane.showMessageDialog(null, "Enter Quantity > 0");
                jtxtQuantity.requestFocusInWindow();
                return;
            }
        }
        catch (NumberFormatException ex)
        {
            JOptionPane.showMessageDialog(null, "Enter number not letter!");
            return;
        }
        
        if (jRadioSingle.isSelected())
        {
         order.setOrder("Single Burger");
        }
        
        if (jRadioDouble.isSelected())
        {
            order.setOrder("Double Burger");
        }
        
        if (jCheckBoxAddBacon.isSelected())
        {
            order.setOrder("add Bacon");
        }
        
        if (jCheckBoxAddCheese.isSelected())
        {
            order.setOrder("w/ Cheese");
        }
        
        if (jCheckBoxMeal.isSelected())
        {
            order.setOrder("make it a Meal");
        }
        
        jTextArea1.append("Qty:" + order.getOrderQty() + " | " + order.getOrder() + " at " + order.getTotal() + "\n");
        jtxtOrder.setText("$" + order.getTotalOrder());
        
        // jTextArea1.append(order.getTotal());
        clearOptions();
        
    }//GEN-LAST:event_jMenuAddActionPerformed

