    private void jCheckBoxMealActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBoxMealActionPerformed
        // TODO add your handling code here:
        
        if (jCheckBoxMeal.isSelected())
        {
            order.setMeal(4.00);
         
        }
        else
        {
            order.setMeal(-4.00);
         
        }
        
        jtxtPrice.setText("$ " + order.getTotal());
    }//GEN-LAST:event_jCheckBoxMealActionPerformed

    private void jCheckBoxAddBaconActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBoxAddBaconActionPerformed
        if (jCheckBoxAddBacon.isSelected())
        {
            order.setExtraBacon(1.25);
            
        }   
        if (!jCheckBoxAddBacon.isSelected())
        {
            order.setExtraBacon(-1.25);
            
        }           
        jtxtPrice.setText("$ " + order.getTotal());
    }//GEN-LAST:event_jCheckBoxAddBaconActionPerformed

    private void calcCurrentPrice()
    {
        if (jCheckBoxAddCheese.isSelected())
        {
            order.setExtraCheese(0.50);
        }  
        else 
        {
            order.setExtraCheese(-0.50);
        }
        
        jtxtPrice.setText("$ " + order.getTotal());
    }
    
    
    private void clearOptions()
    {
        jtxtQuantity.setText("1");
        jtxtPrice.setText("$ 0.00");
        buttonGroupBurgers.clearSelection();
        jCheckBoxAddBacon.setSelected(false);
        jCheckBoxAddCheese.setSelected(false);
        jCheckBoxMeal.setSelected(false);
        order.clearOrder();
    }
    public void clearOrder()
    {
        clearOptions();
        jTextArea1.setText("");
        jtxtOrder.setText("$ 0.00");
        order.clearOrder();
    }
    
